import { Component, OnInit, OnDestroy } from '@angular/core';
import { CarService } from '../services/car.service';
import { Router } from '@angular/router';
import { Subscription, interval } from 'rxjs';

@Component({
  selector: 'app-car-list',
  templateUrl: './car-list.page.html',
  styleUrls: ['./car-list.page.scss'],
})
export class CarListPage implements OnInit, OnDestroy {
  cars: any = [];
  private refreshSubscription: Subscription;

  constructor(private carService: CarService, private router: Router) {}

  ngOnInit() {
    this.loadCars();

    // Configura una suscripción para refrescar la lista cada 60 segundos (ajusta esto según tus necesidades)
    this.refreshSubscription = interval(60000).subscribe(() => {
      this.loadCars();
    });
  }

  ngOnDestroy() {
    // Asegúrate de desuscribirte cuando el componente se destruye para evitar pérdida de memoria
    if (this.refreshSubscription) {
      this.refreshSubscription.unsubscribe();
    }
  }

  loadCars() {
    this.carService.getCars().subscribe((data) => {
      this.cars = data;
    });
  }

  gotoCarAdd() {
    this.router.navigateByUrl('/car-add');
  }

  editCar(carId: number) {
    this.router.navigate(['/car-update', carId]);
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>car-list</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-list>
    <ion-item *ngFor = "let c of cars">
      <ion-label>
        <h1>{{c.marca}}</h1>
        Modelo: {{c.modelo}}
        Precio: {{c.precio}}
        Descripcion: 
        {{c.descripcion}}
      </ion-label>
      <ion-button (click)="editCar(c.id)">Modificar oferta</ion-button>

    </ion-item>
  </ion-list>

  <ion-button (click)="gotoCarAdd()" expand="full">Añadir nuevo coche</ion-button>
</ion-content>
