import { Component, OnInit } from '@angular/core';
import { CarService } from '../services/car.service';

@Component({
  selector: 'app-car-compare',
  templateUrl: './car-compare.page.html',
  styleUrls: ['./car-compare.page.scss'],
})
export class CarComparePage implements OnInit {
  constructor(private carService: CarService) { }

  ngOnInit() {
  }

  selectedCars: any[] = this.carService.getSelectedCars();

  addToComparison(car: any) {
    this.carService.addToComparison(car); 
  }

  removeFromComparison(car: any) {
    this.carService.removeFromComparison(car); 
  }
}



<ion-header>
  <ion-toolbar>
    <ion-title>car-compare</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <ion-item *ngFor="let car of selectedCars">
      <h2>{{ car.Marca }} {{ car.Modelo }} {{ car.Precio }} {{ car.Descripcion }}</h2>
    </ion-item>
  </ion-list>
</ion-content>
