  <ion-header [translucent]="true">
    <ion-toolbar>
      <ion-title>car-list</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-content [fullscreen]="true">
    <ion-list>

      <ion-item *ngFor="let c of cars">
        <ion-label>
          <h1>{{c.marca}}</h1>
          Modelo: {{c.modelo}}
          Precio: {{c.precio}}
          Descripcion: 
          {{c.descripcion}}
        </ion-label>
        <ion-button (click)="editCar(c.id)">Modificar oferta</ion-button>
        <ion-button (click)="deleteCar(c.id)">Eliminar</ion-button>
      </ion-item>
    </ion-list>

    <ion-button (click)="gotoCarAdd()" expand="full">Añadir nuevo coche</ion-button>
  </ion-content>
