import { Component, OnInit } from '@angular/core';
import { CarService } from '../services/car.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-car-update',
  templateUrl: './car-update.page.html',
  styleUrls: ['./car-update.page.scss'],
})
export class CarUpdatePage implements OnInit {

  carId: number = 0;
  car: any = {}; // Objeto para almacenar los datos editados

  constructor(private carService: CarService, private activatedRoute: ActivatedRoute, private router: Router) { }

  ngOnInit() {
    this.carId = +this.activatedRoute.snapshot.paramMap.get('id')!;

    // Obtén los detalles del coche a editar por su ID
    this.carService.getCarById(this.carId).subscribe((data) => {
      this.car = data;
    });
  }  

  updateCar(){
        // Guarda los cambios en el coche
        this.carService.updateCar(this.carId, this.car).subscribe(() => {
          console.log('Cambios guardados');
          // Vuelve a la lista de coches después de guardar
          // Puedes redirigir al usuario o hacer lo que sea necesario
  });
}
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>car-update</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
<!-- Mostrar campos de edición de coche -->
<ion-item>
  <ion-label position="floating">Marca</ion-label>
  <ion-input [(ngModel)]="car.marca" name="marca" type="text"></ion-input>
</ion-item>

<ion-item>
  <ion-label position="floating">Modelo</ion-label>
  <ion-input [(ngModel)]="car.modelo" name="modelo" type="text"></ion-input>
</ion-item>

<ion-item>
  <ion-label position="floating">Precio</ion-label>
  <ion-input [(ngModel)]="car.precio" name="precio" type="number"></ion-input>
</ion-item>

<ion-item>
  <ion-label position="floating">Descripción</ion-label>
  <ion-textarea [(ngModel)]="car.descripcion" name="descripcion"></ion-textarea>
</ion-item>

<!-- Botón para guardar cambios -->
<ion-button (click)="updateCar()">Guardar Cambios</ion-button>

</ion-content>
