import { Component, OnInit } from '@angular/core';
import { CarService } from '../services/car.service';

@Component({
  selector: 'app-car-compare',
  templateUrl: './car-compare.page.html',
  styleUrls: ['./car-compare.page.scss'],
})
export class CarComparePage implements OnInit {

  constructor(private carService: CarService) { }

  ngOnInit() {
  }

  Cars: Car[] = [];

  // Agregar coches a la lista de comparación
  addToComparison(car: Car) {
    this.selectedCars.push(car);
  }

  // Remover coches de la lista de comparación
  removeFromComparison(car: Car) {
    const index = this.selectedCars.indexOf(car);
    if (index !== -1) {
      this.selectedCars.splice(index, 1);
    }
  }
}

<ion-header>
  <ion-toolbar>
    <ion-title>car-compare</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <ion-item *ngFor="let car of selectedCars">
      <h2>{{ car.brand }} {{ car.model }}</h2>
      <!-- Mostrar características del coche aquí -->
    </ion-item>
  </ion-list>
</ion-content>
